script:
function inject(url, onLoadCallback, onErrorCallback, crossOrigin) {
        const script = document.createElement("script");
        if (onLoadCallback) script.onload = onLoadCallback;
        if (onErrorCallback) script.onerror = onErrorCallback;
        if (crossOrigin) script.crossOrigin = crossOrigin;
        script.defer = true;
        script.src = url;
        document.head.appendChild(script);
    }

    function gtag() {
        window.dataLayer = window.dataLayer || [];
        window.dataLayer.push(arguments);
    }

    function loadGoogleAnalytics() {
        gtag('js', new Date());
        gtag('config', 'G-B98SVR621R', {'debug_mode': false});
        inject("https://www.googletagmanager.com/gtag/js?id=G-B98SVR621R");
    }

    function loadYandexMetrika() {
        window.yandex_metrika_callbacks = window.yandex_metrika_callbacks || [];
        window.yandex_metrika_callbacks.push(function () {
            try {
                window.yaCounter331875 = new Ya.Metrika({
                    id: 331875,
                    trackLinks: true,
                    accurateTrackBounce: true
                });
            } catch (e) {
            }
        });
        inject("https://mc.yandex.ru/metrika/watch.js");
    }

    function trackOutboundLinks() {
        document.querySelectorAll('a[target="_blank"]').forEach(function (link) {
            link.addEventListener("click", function () {
                gtag('event', link.hostname, {
                    event_category: 'Outbound Links',
                    event_label: link.href
                });
            });
        });
    }

    window.addEventListener("load", function () {
        loadGoogleAnalytics();
        loadYandexMetrika();
        trackOutboundLinks();
    });
