var stickyStart = 110;
				var seperateSpace = 900;
				window.addEventListener('scroll', ads_scroll);
				var ads1_bottom_border = 1400;
				var ads2_top_border = ads1_bottom_border + seperateSpace;
				var ads2_stick = true;
				function ads_scroll() {

					var scrolled = window.pageYOffset || document.documentElement.scrollTop;

					var body = document.body,
						html = document.documentElement;
					var contentHeight = Math.max(body.scrollHeight, body.offsetHeight, html.clientHeight, html.scrollHeight, html.offsetHeight);

					
					var contentdivHeight = document.getElementById('content').offsetHeight;
					var ads2Height = document.getElementById('adfox_testy_300x600_2').offsetHeight;
					if((ads2_top_border - 174 + ads2Height) > contentdivHeight){
						let diff = Math.round((ads2_top_border - 174 + ads2Height) - contentdivHeight) + 69;
						document.querySelector('.footer').style.marginTop= diff + 'px';
						ads2_stick = false;
					}

					if (scrolled <= stickyStart - 5) {
						document.getElementById('adfox_testy_300x600_1').className = "";
						document.getElementById('adfox_testy_300x600_1').style.top = '0px';
						document.getElementById('adfox_testy_300x600_1').style.position = 'relative';
					} else if (scrolled < ads1_bottom_border && scrolled + 50 > stickyStart) {
						document.getElementById('adfox_testy_300x600_1').className = "fixed";
					} else if (scrolled > ads1_bottom_border - 600 && scrolled + 20 < ads2_top_border) {
						document.getElementById('adfox_testy_300x600_1').className = "";
						document.getElementById('adfox_testy_300x600_1').style.top = (ads1_bottom_border - stickyStart) + 'px';
						document.getElementById('adfox_testy_300x600_2').className = "";
						document.getElementById('adfox_testy_300x600_2').style.position = 'initial';
					} else if (scrolled + 20 >= ads2_top_border && ads2_stick) {
						if(scrolled > contentHeight - 1200){
							document.getElementById('adfox_testy_300x600_2').className = "";
							document.getElementById('adfox_testy_300x600_2').style.top = (contentHeight - 1200 - ads2_top_border) + 'px';
							document.getElementById('adfox_testy_300x600_2').style.position = 'absolute';
						} else {
							document.getElementById('adfox_testy_300x600_2').className = "fixed";
						}
						
					}

				}

